//import { Component } from 'react';
import axios from 'axios';
import React, { useCallback, useState, useEffect } from 'react';



export const ResumeContext = React.createContext({
  result: null,
  loading: false,
  formData: null,
  requestResults: () => {},
  pushHistory: () => {},
});

  export const ResumeProvider = ({ children }) => {
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState(null);

  const requestResults = (value) => {
    setFormData(value);
  }

  const requestData = useCallback(async () => {
    console.log(loading);

    console.log("hhhhhhhh: " + formData);

    await axios.post("http://127.0.0.1:8000/wel", formData)
     .then(res=> {
       setResult({
         totalScore: res.data.totalScore,
         educationScore: res.data.educationScore,
         educationComments: res.data.educationComments,
         experienceScore: res.data.experienceScore,
         experienceComments: res.data.experienceComments,
         formattingScore: res.data.formattingScore,
         formattingComments: res.data.formattingComments,
         name: res.data.name,
         details: res.data.details
       });
     })
     .catch((err) => {
       if(formData == null){
         Promise.resolve();
       }else{
         console.log("got caught in an error");
         setLoading(false);
         console.log(err);
       }
     })
     .finally(() => setLoading(false));

  }, [formData]);


  useEffect(() => {
    if(formData != null){
      //console.log(formData.get("myFile").name);
      //console.log(formData);
      //history.push('/results');
      setLoading(true);
      //requestData();
      //setFormData(null);
    }
}, [formData]);

useEffect(() => {
  if(loading === true && formData != null){
    console.log("loading now: " + loading);
    requestData();
  }
}, [loading]);

  const context = {
    result,
    loading,
    formData,
    requestResults,
  };

  return (
    <ResumeContext.Provider value={context}>
      {typeof children === 'function' ? children(context) : children}
    </ResumeContext.Provider>
  );
};

export default ResumeContext;
