import ResumeContext from './contexts/ResumeContext';
import { useHistory } from 'react-router-dom';
import React, { useContext, useState, useEffect } from 'react';
//import ResumeContext from "../../contexts/ResumeContext";


const Test = () => {
  const history = useHistory();
  const { formData, loading } = useContext(ResumeContext);
  useEffect(() => {
    if(formData != null || loading){
      //console.log(formData.get("myFile").name);
      //console.log(formData);
      history.push('/results');

      //requestData();
      //setFormData(null);
    }
}, [formData]);
  return(
    <div>
    <ResumeContext.Consumer>
             {(context) => {
               if(context.result == null){
                 history.push('/');
                 return;
               }
               if (context.loading) {
                 //history.push('/results');
                 return 'Loading...';
               }
            return (
              <div>
                <h2>File Details:</h2>


    <p>
    			   Total Score: {" "}
    			   {context.result.totalScore}
    			</p>

    <p>
    			   Education Score: {" "}
    			   {context.result.educationScore}
    			</p>

    <p>
    			   Education Comments: {" "}
    			   {context.result.educationComments}
    			</p>

    <p>
    			   Experience Score: {" "}
    			   {context.result.experienceScore}
    			</p>

    <p>
    			   Experience Comments: {" "}
    			   {context.result.experienceComments}
    			</p>

    <p>
    			   Formatting Score: {" "}
    			   {context.result.formattingScore}
    			</p>

    <p>
    			   Formatting Comments: {" "}
    			   {context.result.formattingComments}
    			</p>

    <p>
    			   Name: {" "}
    			   {context.result.name}
    			</p>

    <p>
    			   Details: {" "}
    			   {context.result.details}
    			</p>

              </div>
            );

              /*<label style={{fontSize: "50px", margin:"100px" }}>
                Result in context :
                <pre>
                  {JSON.stringify(context.result, '\t', '')}
                </pre>
              </label>
            );*/
          }}
              {/*<p style={{fontSize: "50px", margin:"100px" }}>Result is: ({context.result.totalScore}})</p>)*/}
    </ResumeContext.Consumer>
    </div>
  );
};

export default Test;
