//import ResumeSearch from '../../assets/ResumeSearch.svg';
//import Underline from '../../assets/Underline.svg';
import { useContext, useRef } from 'react';
import { useHistory } from 'react-router-dom';
import ResumeContext from '../../contexts/ResumeContext';
//import './Upload.scss';

const Upload = () => {
  const history = useHistory();
  const { requestResults, setData } = useContext(ResumeContext);

  return(
    <div>
    <ResumeContext.Consumer>
    {(context) => (
      <button style={{backgroundColor: "red", height: "25px", width: "25px"}}><input id="photo-upload" type="file" multiple="false" onChange={(e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append(
          "myFile",
          e.target.files[0],
          e.target.files[0].name
        );
        console.log("this is the file name: " + e.target.files[0].name);
        setData(e.target.files[0].name);
        requestResults();
      }}/>Upload</button>)}
    </ResumeContext.Consumer>
    </div>
  );
}

  export default Upload;
