import ResumeSearch from '../../assets/ResumeSearch.svg';
import Underline from '../../assets/Underline.svg';
import { AiOutlineCloudUpload } from "react-icons/ai";
import { useContext, useRef, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import ResumeContext from '../../contexts/ResumeContext';
import './Upload.scss';
import axios from 'axios';

const Upload = () => {
  const { formData,  requestResults, setFormData} = useContext(ResumeContext);
  const history = useHistory();
  return(
    <div className="main-div">
    <div className="upload-div">
    <div clasName="upload-left">
    <h1 className="upload-title">We make your resume stand out</h1>
    <img className="underline-image" src={Underline} alt="" />
    <p className="intro-text">We empower our users with the latest
    AI technology to rate their resumes and make sure they stand
    out from everyone else. Try scanning your resume now for free!</p>
    </div>
    <img className="resume-search-image" src={ResumeSearch}
    alt="magnifying glass scanning a resume document" />
    </div>
    <div class='upload-file'>
    <ResumeContext.Consumer>
    {(context) => (
        <label for='input-file'>
          <i><AiOutlineCloudUpload /></i>
          <p className="button-text">Upload Your Resume</p>
          <input id='input-file' type='file' onChange={(e) => {
            e.preventDefault();
            const formData = new FormData();
            formData.append(
              "myFile",
              e.target.files[0],
              e.target.files[0].name
            );
            context.requestResults(formData)
            .then(() =>{
              history.push('/results');
            })
            .catch((err) => {
              console.log(err);
            });

            //history.push('/results');
          }} hidden/>
        </label>)}
      </ResumeContext.Consumer>
      </div>
      </div>
  );
};

export default Upload;


