//import Underline from '../../assets/Underline.svg';
import { useContext, useRef } from 'react';
import { useHistory } from 'react-router-dom';
import ResumeContext from '../../contexts/ResumeContext';
//import './Upload.scss';
import axios from 'axios';

const Upload = () => {
  const history = useHistory();
  //const { requestResults, setData } = useContext(ResumeContext);

  return(
    <div>
    <ResumeContext.Consumer>
    {(context) => (
      <input type="file" multiple="false" onChange={(e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append(
          "myFile",
          e.target.files[0],
          e.target.files[0].name
        );
        console.log("This is the form data" + formData.get("myFile").name);
        //console.log("this is the file name: " + e.target.files[0].name);
        console.log("type: " + typeof formData === Object)
        context.setFormData(formData);
        context.requestResults();
      }}/> )}
    </ResumeContext.Consumer>
    </div>
  );
}

  export default Upload;
